SELECT apellido1, apellido2, nombre FROM persona WHERE tipo LIKE 'alumno' ORDER BY apellido1, apellido2, nombre ASC;
SELECT nombre, apellido1, apellido2 FROM persona WHERE tipo LIKE 'alumno' AND telefono IS NULL;
SELECT nombre, apellido1, apellido2 FROM persona WHERE tipo LIKE 'alumno' AND fecha_nacimiento LIKE '1999%';
SELECT nombre, apellido1, apellido2 FROM persona WHERE tipo LIKE 'profesor' AND nif LIKE '%k' AND telefono IS NULL;
SELECT nombre FROM asignatura WHERE cuatrimestre = 1 AND curso = 3 AND id_grado = 7;
SELECT persona.apellido1, persona.apellido2, persona.nombre, departamento.nombre FROM persona INNER JOIN profesor ON persona.id = profesor.id_profesor INNER JOIN departamento ON profesor.id_departamento = departamento.id ORDER BY persona.apellido1, persona.apellido2, persona.nombre ASC;
SELECT persona.nif, asignatura.nombre, curso_escolar.anyo_inicio, curso_escolar.anyo_fin FROM persona INNER JOIN alumno_se_matricula_asignatura ON alumno_se_matricula_asignatura.id_alumno = persona.id INNER JOIN asignatura ON alumno_se_matricula_asignatura.id_asignatura = asignatura.id INNER JOIN curso_escolar ON alumno_se_matricula_asignatura.id_curso_escolar = curso_escolar.id WHERE persona.nif LIKE '26902806M';
SELECT DISTINCT departamento.nombre AS 'Nombre Departamento' FROM departamento INNER JOIN profesor ON departamento.id = profesor.id_departamento INNER JOIN asignatura ON asignatura.id_profesor = profesor.id_profesor INNER JOIN grado ON asignatura.id_grado = grado.id WHERE grado.nombre LIKE '%informatica%';
SELECT DISTINCT persona.nombre, persona.apellido1, persona.apellido2 FROM persona INNER JOIN alumno_se_matricula_asignatura ON alumno_se_matricula_asignatura.id_alumno = persona.id INNER JOIN asignatura ON alumno_se_matricula_asignatura.id_asignatura = asignatura.id INNER JOIN curso_escolar ON alumno_se_matricula_asignatura.id_curso_escolar = curso_escolar.id WHERE curso_escolar.anyo_inicio = 2018;
SELECT departamento.nombre, persona.apellido1, persona.apellido2, persona.nombre FROM profesor LEFT JOIN departamento ON profesor.id_departamento = departamento.id  LEFT JOIN persona ON persona.id = profesor.id_profesor ORDER BY departamento.nombre, persona.apellido1, persona.apellido2, persona.nombre ASC;
SELECT departamento.nombre, persona.apellido1, persona.apellido2, persona.nombre FROM departamento RIGHT JOIN profesor ON profesor.id_departamento = departamento.id  RIGHT JOIN persona ON persona.id = profesor.id_profesor WHERE persona.tipo LIKE 'profesor' AND profesor.id_departamento IS NULL;
SELECT departamento.nombre, persona.apellido1, persona.apellido2, persona.nombre FROM departamento LEFT JOIN profesor ON profesor.id_departamento = departamento.id  LEFT JOIN persona ON persona.id = profesor.id_profesor WHERE profesor.id_departamento IS NULL;
SELECT asignatura.nombre, persona.apellido1, persona.apellido2, persona.nombre FROM asignatura RIGHT JOIN profesor ON profesor.id_profesor = asignatura.id_profesor  RIGHT JOIN persona ON persona.id = profesor.id_profesor WHERE persona.tipo LIKE 'profesor' AND asignatura.id_profesor IS NULL;
SELECT asignatura.nombre, persona.apellido1, persona.apellido2, persona.nombre FROM asignatura LEFT JOIN profesor ON profesor.id_profesor = asignatura.id_profesor  LEFT JOIN persona ON persona.id = profesor.id_profesor WHERE asignatura.id_profesor IS NULL;
SELECT DISTINCT departamento.nombre FROM departamento LEFT JOIN profesor ON profesor.id_departamento = departamento.id  LEFT JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor LEFT JOIN alumno_se_matricula_asignatura ON alumno_se_matricula_asignatura.id_asignatura = asignatura.id LEFT JOIN curso_escolar ON curso_escolar.id = alumno_se_matricula_asignatura.id_curso_escolar WHERE asignatura.id_profesor IS NULL AND alumno_se_matricula_asignatura.id_curso_escolar IS NULL;
SELECT COUNT(persona.id) AS 'Nombre total alumnes' FROM persona WHERE persona.tipo LIKE 'alumno';
SELECT COUNT(persona.id) AS 'Total alumnes 1999' FROM persona WHERE persona.tipo LIKE 'alumno' AND persona.fecha_nacimiento LIKE '1999%';
SELECT departamento.nombre, COUNT(profesor.id_profesor) AS 'Nombre profesor' FROM profesor INNER JOIN departamento ON profesor.id_departamento = departamento.id GROUP BY departamento.nombre ORDER BY COUNT(profesor.id_profesor) DESC;
SELECT departamento.nombre, COUNT(profesor.id_profesor) AS 'Nombre profesor' FROM profesor RIGHT JOIN departamento ON profesor.id_departamento = departamento.id GROUP BY departamento.nombre ORDER BY COUNT(profesor.id_profesor) DESC;
SELECT grado.nombre AS 'Nom graus', COUNT(asignatura.id) AS 'Nombre asignatures' FROM grado LEFT JOIN asignatura ON asignatura.id_grado = grado.id GROUP BY grado.nombre ORDER BY COUNT(asignatura.id) DESC;
SELECT grado.nombre AS 'Nom graus', COUNT(asignatura.id) AS 'Nombre asignatures' FROM grado INNER JOIN asignatura ON asignatura.id_grado = grado.id GROUP BY grado.nombre HAVING COUNT(asignatura.id) > 40;
SELECT grado.nombre AS 'Nom graus', asignatura.tipo AS 'Tipus assignatura', SUM(asignatura.creditos) AS 'Suma creditos' FROM asignatura INNER JOIN grado ON asignatura.id_grado = grado.id GROUP BY grado.nombre, asignatura.tipo;
SELECT curso_escolar.anyo_inicio AS 'Curs escolar', COUNT(alumno_se_matricula_asignatura.id_alumno) AS 'Nombre alumnes' FROM alumno_se_matricula_asignatura RIGHT JOIN curso_escolar ON alumno_se_matricula_asignatura.id_curso_escolar = curso_escolar.id GROUP BY curso_escolar.anyo_inicio ;
SELECT persona.id, persona.nombre, persona.apellido1, persona.apellido2, COUNT(asignatura.id_profesor) AS 'Nombre assignatures' FROM profesor LEFT JOIN persona ON persona.id = profesor.id_profesor LEFT JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor GROUP BY profesor.id_profesor ORDER BY COUNT(asignatura.id_profesor) DESC;
SELECT * FROM persona WHERE persona.tipo LIKE 'alumno' ORDER BY persona.fecha_nacimiento DESC LIMIT 1;
SELECT persona.id, persona.nombre, persona.apellido1, persona.apellido2 FROM profesor LEFT JOIN persona ON persona.id = profesor.id_profesor LEFT JOIN departamento ON profesor.id_departamento = departamento.id LEFT JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor WHERE asignatura.id_profesor IS NULL AND profesor.id_departamento IS TRUE;
